sys_ui_action.script:
//Client-side 'onclick' function
function checkUIAction(){
	if((g_form.getValue('u_test_ui_action')=='') || (isNaN(g_form.getValue('u_test_ui_action'))== false)){
		try {g_form.hideFieldMsg('u_test_ui_action');} catch(e) {}
		g_form.setMandatory('u_test_ui_action',true);
		g_form.showFieldMsg('u_test_ui_action','Must be number and not null !!!','error');
		return false;
	}

	//Call the UI Action and skip the 'onclick' function   
	gsftSubmit(null, g_form.getFormElement(), 'resolveIncident'); 


	//Code that runs without 'onclick'
	//Ensure call to server-side function with no browser errors
	if(typeof window == 'undefined'){
		serverResolve();
	}

	//Server-side function 
	function runBusRuleCode(){   
		current.u_test_ui_action=this.getValue('u_test_ui_action');   
		current.update();
	}
}
